import { IonButton, IonButtons, IonCard, IonCol, IonContent, IonGrid, IonHeader, IonIcon, IonInfiniteScroll, IonInfiniteScrollContent, IonInput, IonItem, IonList, IonMenuButton, IonPage, IonRow, IonSearchbar, IonTitle, IonToolbar } from '@ionic/react';
import { add, close, pencil } from 'ionicons/icons';
import { useState, useEffect } from 'react';
import { useHistory, useParams, useRouteMatch } from 'react-router';
import ExploreContainer from '../../components/ExploreContainer';
import Product from './Product';

//require('./jm.json')
let data: Product[] = require('./jm.json');


const ProductsJMList: React.FC = () => {

    const { name } = useParams<{ name: string; }>();


    const history = useHistory();


    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [searchText, setSearchText] = useState('');

    const [isInfiniteDisabled, setInfiniteDisabled] = useState(false);


    useEffect(() => {
    }, [history.location.pathname, searchText]);
    /* 
    const search = async ()=> {
        let result = await searchSuppliers();
        setProveedores(result);

    }
   const remove = async(id:string)=>{
        await removeSupplier(id);
        search();
    }
    const addSupplier = () =>{
        history.push('/page/supplier/new')
    }

    const editSupplier = (id:string)=> {
        history.push('/page/supplier/'+id);
    }
    */
    const loadData = (ev: any) => {
        setTimeout(() => {
            let pp = 50;
            setRowsPerPage(500);
            console.log('Loaded data');
            ev.target.complete();
        }, 50);
    }
    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonButtons slot="start">
                        <IonMenuButton />
                    </IonButtons>
                    <IonTitle>{name}</IonTitle>
                </IonToolbar>
            </IonHeader>

            <IonContent fullscreen>
                <IonHeader collapse="condense">
                    <IonToolbar>
                        <IonTitle size="large">{name}</IonTitle>
                    </IonToolbar>
                    <IonTitle size='large'>JM</IonTitle>
                </IonHeader>


                <IonContent>
                    <IonSearchbar value={searchText} onIonChange={(e: any) => setSearchText(e.detail.value)} placeholder="Buscar..." showCancelButton="focus"></IonSearchbar>
                    <IonCard>
                        <IonTitle>Gestion de productos</IonTitle>

                        <IonItem>
                            <IonButton color='primary' fill='solid' slot='end' size='default'>
                                <IonIcon icon={add} />
                                Agregar proveedor
                            </IonButton>
                        </IonItem>

                        <IonGrid className='table' >
                            <IonRow>
                                <IonCol>Codigo</IonCol>
                                <IonCol>Descripcion</IonCol>
                                <IonCol>Precio</IonCol>
                            </IonRow>

                            {data
                                .filter(
                                    (producto: Product) => {

                                        return (producto.description?.toString().toLocaleLowerCase().includes(searchText.toLowerCase()) || (producto.cod?.toString().toLocaleLowerCase().includes(searchText.toLowerCase())))
                                    })

                                .slice(0, rowsPerPage)
                                .map((producto:Product) =>{
                                <IonList key={producto.id}>

                                    <IonRow>
                                        <IonCol>{producto.cod} </IonCol>
                                        <IonCol>{producto.description}</IonCol>
                                        <IonCol>{Number(producto.price) * 1.5}</IonCol>

                                    </IonRow>
                                </IonList>
                                }
                                )}
                        <IonInfiniteScroll
                            onIonInfinite={loadData}
                            threshold="100px"
                            disabled={isInfiniteDisabled}

                        />
                        <IonInfiniteScrollContent
                            loadingSpinner="bubbles"
                            loadingText="Loading more data..."
                        ></IonInfiniteScrollContent>
                        </IonGrid>
                    </IonCard>
                </IonContent>






            </IonContent>
        </IonPage>
    );
};


export default ProductsJMList;
