import { IonButton, IonButtons, IonCard, IonCol, IonContent, IonFab, IonFabButton, IonFabList, IonGrid, IonHeader, IonIcon, IonInfiniteScroll, IonInfiniteScrollContent, IonInput, IonItem, IonList, IonMenuButton, IonPage, IonRow, IonSearchbar, IonSplitPane, IonTitle, IonToolbar } from '@ionic/react';
import { add, car, cart, close, logoVimeo, pencil } from 'ionicons/icons';
import { useState, useEffect } from 'react';
import { useHistory, useParams, useRouteMatch } from 'react-router';
import Cart from '../../components/cart/Cart';
import ExploreContainer from '../../components/ExploreContainer';
import Product from './Product';
import taladro from './ReaderTaladro/ReaderTaladro';


const data: Product[] = require('./Taladro.json');
const ProductsTaladroList: React.FC = () => {

    const { name } = useParams<{ name: string; }>();


    const history = useHistory();


    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(100);
    const [searchText, setSearchText] = useState('');

    const [isInfiniteDisabled, setInfiniteDisabled] = useState(false);
    const [productToCart,setProductToCart] = useState<Product>({});


    useEffect(() => {
        console.log(productToCart.description)
    }, [history.location.pathname, searchText]);
    /* 
    const search = async ()=> {
        let result = await searchSuppliers();
        setProveedores(result);

    }
   const remove = async(id:string)=>{
        await removeSupplier(id);
        search();
    }
    const addSupplier = () =>{
        history.push('/page/supplier/new')
    }

    const editSupplier = (id:string)=> {
        history.push('/page/supplier/'+id);
    }
    */

    const loadData = (ev: any) => {
        setTimeout(() => {

            setRowsPerPage(rowsPerPage + 100);
            console.log('Loaded data');
            ev.target.complete();
        }, 500);
    }

    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonButtons slot="start">
                        <IonMenuButton />
                    </IonButtons>

                </IonToolbar>
            </IonHeader>

            <IonContent fullscreen>
                <IonHeader collapse="condense">
                    <IonToolbar>
                        <IonTitle size="large">{name}</IonTitle>
                    </IonToolbar>
                    <IonFab className='floatingButton' vertical="center" horizontal="end" edge slot="">
                        <IonFabButton>
                            <IonIcon icon={cart} />
                        </IonFabButton>
                        <IonFabList side="start">
                            <Cart product={productToCart}></Cart>
                        </IonFabList>
                    </IonFab>
                </IonHeader>



                <IonContent>
                    <IonSearchbar value={searchText} onIonChange={(e: any) => setSearchText(e.detail.value)} placeholder="Buscar..." showCancelButton="focus"></IonSearchbar>
                    <IonCard>
                        <IonTitle>Lista El Taladro</IonTitle>

                        <IonItem>
                            <IonButton color='primary' fill='solid' slot='end' size='default'>
                                <IonIcon icon={add} />
                                Agregar proveedor
                            </IonButton>
                        </IonItem>

                        <IonGrid className='table'>
                            <IonRow>
                                <IonCol>Codigo</IonCol>
                                <IonCol>Descripcion</IonCol>
                                <IonCol>Precio</IonCol>
                            </IonRow>

                            {data
                                /*.filter(
                                    ({ cod, description }: Product) =>{
                                    (cod!=undefined && description!=undefined)?
                                      (cod.toString().toLocaleLowerCase() ||
                                      description.toString().toLocaleLowerCase().includes(searchText) 
                                  ):(console.log("error"))}
                                  )*/
                                .filter(
                                    (producto: Product) => {

                                        return (producto.description?.toString().toLocaleLowerCase().includes(searchText.toLocaleLowerCase()) || (producto.cod?.toString().toLocaleLowerCase().includes(searchText.toLocaleLowerCase())))
                                    })
                                .slice(0, rowsPerPage)
                                .map((producto: Product, id: number) =>
                                    <IonCard className='tarjeta'>

                                        <IonRow key={id++} >
                                            <IonCol >{producto.cod} </IonCol>
                                            <IonCol>{producto.description}</IonCol>
                                            <IonCol>{"$" + Number(producto.price).toFixed()}</IonCol>
                                            <IonButtons>
                                                <IonButton size='small' fill='solid' color='success'  onClick={(e)=>{
                                                    setProductToCart(producto)
                                                    console.log(productToCart.description)
                                                    }}>

                                                    <IonIcon icon={add}/>
                                                </IonButton>
                                            </IonButtons>
                                        </IonRow>
                                    </IonCard>

                                )}
                            <IonInfiniteScroll
                                onIonInfinite={loadData}
                                threshold="100px"
                                disabled={isInfiniteDisabled}

                            >
                                <IonInfiniteScrollContent
                                    loadingSpinner="bubbles"
                                    loadingText="Cargando Productos..."
                                ></IonInfiniteScrollContent>
                            </IonInfiniteScroll>
                        </IonGrid>
                    </IonCard>
                </IonContent>





            </IonContent>
        </IonPage>
    );
};

export default ProductsTaladroList;
