import {
  IonButton,
  IonButtons,
  IonCard,
  IonCardHeader,
  IonCardTitle,
  IonCol,
  IonContent,
  IonGrid,
  IonIcon,
  IonImg,
  IonItem,
  IonItemDivider,
  IonLabel,
  IonList,
  IonListHeader,
  IonMenu,
  IonMenuToggle,
  IonNote,
  IonRow,
  IonSplitPane,
  IonTitle,
} from "@ionic/react";

import { useHistory, useLocation } from "react-router-dom";
import {
  add,
} from "ionicons/icons";

import { useContext, useEffect, useState } from "react";
import Product from "../../interfaces/Product";
import ProductCart from "../../interfaces/ProductCart";
import CartProvider from "../../context/CartProvider";
import { CartContext } from "../../context/CartContext";
import CartItem from "./CartItem";

function Cart() {
  const [amount, setAmount] = useState<number>(0);
  const [cartItems, setCartItems] = useState<ProductCart[]>([]);
  const [suma, setSuma] = useState(0);
  const history = useHistory();

  useEffect(() => {
  }, [cartItems]);

  const { cartState} = useContext(CartContext);

  return (
      <IonCard className="itemCart">
        <IonCardTitle className="title-card" color="primary" mode="ios">
          Compra
        </IonCardTitle>
        <IonItemDivider />
        {cartState.products?.map((item: ProductCart) => (
          <IonRow key={item.id} className="fila">
            <IonCol>{item.description}</IonCol>
            {item.brand === undefined ? (
              <IonCol>{item.price}</IonCol>
            ) : (
              <IonCol>{(Number(item.price) * 1.5).toFixed()}</IonCol>
            )}
            <IonCol> {item.amount}</IonCol>
            <IonButtons>
              <IonButton
                fill="solid"
                color="success"
              >
                <IonIcon icon={add} />
              </IonButton>
            </IonButtons>
          </IonRow>
        ))}
        <IonItem>
          <IonTitle className="title-card">
            Productos {cartState.products.length}
          </IonTitle>
          {cartState.products.length !== 0 ? (
            <IonTitle>Suma: {suma.toFixed()}</IonTitle>
          ) : (
            <IonLabel color="primary">No hay productos cargados</IonLabel>
          )}
        </IonItem>
        <IonItemDivider />
        <IonButtons>
          <IonButton color="success" size="default" fill="solid">
            Venta
          </IonButton>
          <IonButton fill="solid" color="primary">
            A Cuenta
          </IonButton>
        </IonButtons>
      </IonCard>
  );
}

export default Cart;
