import {
    IonButton,
    IonButtons,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCol,
    IonContent,
    IonGrid,
    IonIcon,
    IonImg,
    IonItem,
    IonLabel,
    IonList,
    IonListHeader,
    IonMenu,
    IonMenuToggle,
    IonNote,
    IonRow,
    IonSplitPane,
    IonTitle,
  } from '@ionic/react';

  import { useHistory, useLocation } from 'react-router-dom';
  import { archiveOutline, archiveSharp, bookmarkOutline, heartOutline, heartSharp, mailOutline, mailSharp, paperPlaneOutline, paperPlaneSharp, trashOutline, trashSharp, warningOutline, warningSharp,peopleOutline, peopleSharp, cart } from 'ionicons/icons';
import Product from '../../pages/products/Product';
import { useEffect, useState } from 'react';


interface ContainerProps {
    product: Product;
  }
  const Cart: React.FC<ContainerProps> = (props:{product:Product}) => {
    const {product} =props;

    const [cartItems,setCartItems] = useState<Product[]>([]);
    const [suma,setSuma] =useState(0);
    const location = useLocation();
    const history = useHistory();
    useEffect(()=>{
        cargaItems(product)
        Sumar(cartItems);

    },[cartItems,props.product]);
const cargaItems= (product:Product)=>{
    if(product.description!=undefined){
        let aux:Product[]=cartItems
        aux.push(product)
        setCartItems(aux);
        console.log('cargando' + product.description)
    }
}  
const Sumar = (items:Product[])=>{
    let sum=0;
    items.forEach((product)=>{
        sum+=Number(product.price);
    });
    setSuma(sum);

}
    return (
        <IonGrid>
            <IonCard className='itemCart'>
                        <IonCardTitle>Compra</IonCardTitle>
                {cartItems.map((item:Product) =>
                        <IonRow key={item.id} className='fila'>
                            <IonCol>{item.description}</IonCol>
                    {
                        (item.brand===undefined)?
                        <IonCol>{item.price}</IonCol>
                    :
                    <IonCol>{Number(item.price)*1.5}</IonCol>
                    }
                        </IonRow>


                )
                }
                <IonItem>
                    <IonTitle>

                    Productos {cartItems.length}
                    </IonTitle>
                    {
                        (cartItems.length!==0)?
                        <IonLabel>
                         {suma}
                       </IonLabel>
                    : <IonLabel color='primary' hidden={true}>
                        No hayproductos cargados</IonLabel>}
                </IonItem>

            <IonButtons>
                <IonButton color='success' size='default' fill='solid'>
                    Venta
                </IonButton>
                <IonButton fill='solid' color='primary'>A Cuenta</IonButton>
            </IonButtons>
            </IonCard>
        </IonGrid>

    )
  };

  export default Cart;
