export const searchCostumers = ()=>{
    if(!localStorage['customers']){
        localStorage['customers']= '[]';
    }

    let customers = localStorage['customers'];
    customers = JSON.parse(customers);
    return customers;


    const datosEjemplo = [
        {
            id: 1,
            firstname: 'Jero',
            lastname: 'Jimenez',
            phone: '22311451',
            email: 'jerodepiran@gmail.com',
            address: 'San Martin 190',
        },
        {
            id: 2,
            firstname: 'Oscar',
            lastname: 'Perez',
            phone: '22311451',
            email: 'jerojimenez17@gmail.com',
            address: 'Belgrano 123',
        },
        {
            id: 3,
            firstname: 'Pedro',
            lastname: 'Lopez',
            phone: '2266525',
            email: 'pedrolopez@gmail.com',
            address: '12 de octubre 123',
        }
    ];
}
export const removeCostumer = (id:string)=>{
    let customers = searchCostumers();
    let indice = customers.findIndex((customer:any) => customer.id == id);
    customers.splice(indice,1);
    localStorage['customers'] = JSON.stringify(customers);
}

export const saveCostumer = (customer:any)=>{
    let customers = searchCostumers();
    customers.push(customer);
    localStorage['customers'] = JSON.stringify(customers);
}
