import { IonButton, IonButtons, IonCard, IonCol, IonContent, IonGrid, IonHeader, IonIcon, IonItem, IonMenuButton, IonPage, IonRow, IonTitle, IonToolbar } from '@ionic/react';
import { add, close, pencil } from 'ionicons/icons';
import { useState, useEffect } from 'react';
import { useHistory, useParams } from 'react-router';
import ExploreContainer from '../../components/ExploreContainer';
import Employee from './Employee';
import {removeCostumer, searchCostumers } from './EmployeeApi';


const CustomerList: React.FC = () => {

    const { name } = useParams<{ name: string; }>();

    const [clientes,setClientes] = useState<Employee[]>([]);

    const history = useHistory();

    useEffect(() => {
        search();
    }, [history.location.pathname]);

    const search = ()=> {
        let result = searchCostumers();
        setClientes(result);

    }
    const remove = (id:string)=>{
        removeCostumer(id);
        search();
    }
    const addCustomer = () =>{
        history.push('/page/employee/new')
    }

    const editCustomer = (id:string)=> {
        history.push('/page/employee/'+id);
    }

    return (
        <IonPage>




                <IonContent>
                    <IonCard>
                    <IonTitle>Gestion de Clientes</IonTitle>

                    <IonItem>
                        <IonButton onClick={addCustomer} color='primary' fill='solid' slot='end' size='default'>
                            <IonIcon icon={add}/>
                            Agregar Cliente
                        </IonButton>
                    </IonItem>

                        <IonGrid className='table'>
                            <IonRow>
                                <IonCol>Nombre</IonCol>
                                <IonCol>Email</IonCol>
                                <IonCol>Telefono</IonCol>
                                <IonCol>Direccion</IonCol>
                                <IonCol>Acciones</IonCol>
                            </IonRow>

                            {clientes.map((cliente:Employee) =>

                            <IonRow>
                                <IonCol>{cliente.firstname +' ' +  cliente.lastname} </IonCol>
                                <IonCol>{cliente.email}</IonCol>
                                <IonCol>{cliente.phone}</IonCol>
                                <IonCol>{cliente.address}</IonCol>
                                <IonCol>
                                    <IonButton onClick={()=>editCustomer(String(cliente.id))} color='primary' fill='clear'>
                                        <IonIcon icon={pencil} slot='icon-only'/>
                                    </IonButton>
                                    <IonButton color='danger' fill='clear'
                                        onClick={()=>remove(String(cliente.id))}>
                                        <IonIcon icon={close} slot='icon-only'/>
                                    </IonButton>
                                </IonCol>
                            </IonRow>
                            )}

                        </IonGrid>
                    </IonCard>
                </IonContent>






        </IonPage>
    );
};

export default CustomerList;
