// import CartState from "../interfaces/CartState";
import CartState from "../interfaces/CartState";
import Product from "../interfaces/Product";
import ProductCart from "../interfaces/ProductCart";

type CartAction = 
   | {type:'addItem', payload: ProductCart}
   | {type:'removeItem', payload: number}
    | {type:'removeAll', payload: Product}
    | {type:'updateAmount', payload: {id:string}}
    | {type:'updateTotal', payload: ProductCart};

const toProductCart= (product: Product):ProductCart => {
    return {
        ...product,
        amount: 0,
    }
}


export const CartReducer = (state: CartState, action: CartAction) => {
    switch(action.type){
         case 'addItem':

             const pay=toProductCart(action.payload);
             return {
                 ...state,
                 products: [...state.products, pay],
                 amount: state.amount + 1,
                 total: state.total + parseFloat(pay.price)
             }
        case 'removeItem':
            return {
                ...state,
                products:[...state.products.slice(0,action.payload), ...state.products.slice(action.payload+1)],
                amount: state.amount - 1,
                total: state.total - parseFloat(state.products[action.payload].price)
            }
        case 'removeAll':
            return {
                ...state,
                products:[],  
                amount: 0,
                total: 0  
            }
        case 'updateAmount':
            return {
                ...state,
                products: state.products.map(product => {
                    if(product.id === Number( action.payload.id)){
                        state.amount = state.amount + 1;
                        state.total = state.total + parseFloat(product.price);
                        return {
                            ...product,
                            amount: product.amount + 1
                        }
                    }
                    return product;
                }),    
            }

        default:
            return state;

    }
}
